package weel1.day2;

public class IsPrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int n1=2;n1<10;n1++) {
			if(n1%2==0) {
				System.out.println(n1+"" +"is not a prime number");
				}
				else {
					System.out.println(n1+""+"is a prime number");
				}
			
}
	}
}